contracts/SignaturesUpgradeable.sol.content:
// SPDX-License-Identifier: LGPL-3.0-only
pragma solidity 0.8.11;
import "./interfaces/IBridge.sol";
import "./utils/PausableUpgradeable.sol";
import {AccessControlUpgradeable as AccessControl} from "./utils/AccessControlUpgradeable.sol";
import "./utils/SafeCast.sol";
import "@openzeppelin/contracts-upgradeable/utils/cryptography/ECDSAUpgradeable.sol";

contract SignaturesUpgradeable is PausableUpgradeable, AccessControl {
    using SafeCast for *;
    // 0xc4cb5d35714699d6e85b9562b644e60393b418d974a5c1dd8efaadac37a142c5
    bytes32 public constant PERMIT_TYPEHASH =
        keccak256(
            "PermitBridge(uint8 domainID,uint64 depositNonce,bytes32 resourceID,bytes data)"
        );
    // 0x8b73c3c69bb8fe3d512ecc4cf759cc79239f7b179b0ffacaa9a75d522b39400f
    bytes32 public constant _TYPE_HASH =
        keccak256(
            "EIP712Domain(string name,string version,uint256 chainId,address verifyingContract)"
        );
    bytes32 public constant RELAYER_ROLE = keccak256("RELAYER_ROLE");
    // 0x058443738ec3641a3233a9f285e16671e4ad9755445580d761017e695f75052b
    bytes32 private constant _HASHED_NAME = keccak256(bytes("PermitBridge"));
    // 0xe6bbd6277e1bf288eed5e8d1780f9a50b239e86b153736bceebccf4ea79d90b3
    bytes32 private constant _HASHED_VERSION = keccak256(bytes("1.0"));

    mapping(uint8 => uint8) public _relayerThreshold;
    mapping(uint8 => uint256) public destChainId;

    function initialize(address admin) public initializer {
        _setupRole(DEFAULT_ADMIN_ROLE, admin);
    }

    modifier onlyAdmin() {
        require(
            hasRole(DEFAULT_ADMIN_ROLE, _msgSender()),
            "sender doesn't have admin role"
        );
        _;
    }

    function _buildDomainSeparator(
        bytes32 typeHash,
        bytes32 nameHash,
        bytes32 versionHash,
        uint256 chainId,
        address signatureContract
    ) private pure returns (bytes32) {
        return
            keccak256(
                abi.encode(
                    typeHash,
                    nameHash,
                    versionHash,
                    chainId,
                    signatureContract
                )
            );
    }

    function _hashTypedDataV4(
        bytes32 structHash,
        uint256 chainId,
        address signatureContract
    ) internal view virtual returns (bytes32) {
        return
            ECDSAUpgradeable.toTypedDataHash(
                _buildDomainSeparator(
                    _TYPE_HASH,
                    _HASHED_NAME,
                    _HASHED_VERSION,
                    chainId,
                    signatureContract
                ),
                structHash
            );
    }

    function checkSignature(
        uint8 domainID,
        uint8 destinationDomainID,
        uint64 depositNonce,
        bytes32 resourceID,
        bytes calldata data,
        bytes calldata signature
    ) public view returns (address) {
        bytes32 structHash = keccak256(
            abi.encode(
                PERMIT_TYPEHASH,
                domainID,
                depositNonce,
                resourceID,
                keccak256(data)
            )
        );
        address destinationBridge = destinationBridgeAddress[
            destinationDomainID
        ];
        bytes32 hash = _hashTypedDataV4(
            structHash,
            destChainId[destinationDomainID] == 0
                ? uint256(destinationDomainID)
                : destChainId[destinationDomainID],
            destinationBridge
        );
        return ECDSAUpgradeable.recover(hash, signature);
    }

    /// @notice depositHash = keccak256(abi.encode(originDomainID,destinationDomainID,depositNonce,resourceID,keccak256(data)));
    /// @notice depositHash => signature[]
    mapping(bytes32 => bytes[]) public signatures;
    /// @notice signature => bool
    mapping(bytes => bool) public hasVote; // scrap
    mapping(bytes32 => mapping(address => bool)) public relayerVote;
    mapping(uint8 => address) public destinationBridgeAddress;

    event SubmitSignature(
        uint8 indexed originDomainID,
        uint8 indexed destinationDomainID,
        uint64 depositNonce,
        bytes32 indexed resourceID,
        bytes data,
        bytes signature
    );

    event SignaturePass(
        uint8 indexed originDomainID,
        uint8 indexed destinationDomainID,
        uint64 depositNonce,
        bytes32 indexed resourceID,
        bytes data,
        bytes signature
    );

    function adminChangeRelayerThreshold(
        uint8 destinationDomainID,
        uint256 newThreshold
    ) external onlyAdmin {
        _relayerThreshold[destinationDomainID] = newThreshold.toUint8();
    }

    function adminSetDestChainId(
        uint8 destinationDomainID,
        uint256 chainId,
        address destinationBridge
    ) external onlyAdmin {
        destChainId[destinationDomainID] = chainId;
        destinationBridgeAddress[destinationDomainID] = destinationBridge;
    }

    function adminPause() external onlyAdmin {
        _pause(_msgSender());
    }

    function adminUnpause() external onlyAdmin {
        _unpause(_msgSender());
    }

    struct Proposal {
        uint8 originDomainID;
        uint8 destinationDomainID;
        address destinationBridge;
        uint64 depositNonce;
        bytes32 resourceID;
        bytes data;
        uint256 proposalIndex;
    }

    uint256 public proposalIndex;
    mapping(bytes32 => Proposal) public proposals;
    mapping(uint256 => bytes32) public indexToProposal;

    function submitSignature(
        uint8 originDomainID,
        uint8 destinationDomainID,
        uint64 depositNonce,
        bytes32 resourceID,
        bytes calldata data,
        bytes calldata signature
    ) external whenNotPaused {
        address destinationBridge = destinationBridgeAddress[
            destinationDomainID
        ];
        address relayer = checkSignature(
            originDomainID,
            destinationDomainID,
            depositNonce,
            resourceID,
            data,
            signature
        );
        require(hasRole(RELAYER_ROLE, relayer), "invalid signature");
        bytes32 depositHash = keccak256(
            abi.encode(
                originDomainID,
                destinationDomainID,
                depositNonce,
                resourceID,
                keccak256(data)
            )
        );
        require(!relayerVote[depositHash][relayer], "signature aleardy submit");
        relayerVote[depositHash][relayer] = true;
        require(
            signatures[depositHash].length <
                _relayerThreshold[destinationDomainID],
            "Signture aleardy pass"
        );
        if (signatures[depositHash].length == 0) {
            proposalIndex++;
            proposals[depositHash] = Proposal({
                originDomainID: originDomainID,
                destinationDomainID: destinationDomainID,
                destinationBridge: destinationBridge,
                depositNonce: depositNonce,
                resourceID: resourceID,
                data: data,
                proposalIndex: proposalIndex
            });
            indexToProposal[proposalIndex] = depositHash;
        }
        signatures[depositHash].push(signature);

        emit SubmitSignature(
            originDomainID,
            destinationDomainID,
            depositNonce,
            resourceID,
            data,
            signature
        );
        if (
            signatures[depositHash].length >=
            _relayerThreshold[destinationDomainID]
        ) {
            emit SignaturePass(
                originDomainID,
                destinationDomainID,
                depositNonce,
                resourceID,
                data,
                signature
            );
        }
    }

    function getSignatures(
        uint8 domainID,
        uint8 destinationDomainID,
        uint64 depositNonce,
        bytes32 resourceID,
        bytes calldata data
    ) external view returns (bytes[] memory) {
        return
            signatures[
                keccak256(
                    abi.encode(
                        domainID,
                        destinationDomainID,
                        depositNonce,
                        resourceID,
                        keccak256(data)
                    )
                )
            ];
    }

    function getProposal(uint256 index) public view returns (Proposal memory) {
        require(index <= proposalIndex, "Proposal not exist");
        return proposals[indexToProposal[index]];
    }

    function getSignatures(uint256 index)
        external
        view
        returns (bytes[] memory)
    {
        require(index <= proposalIndex, "Proposal not exist");
        return signatures[indexToProposal[index]];
    }
}

contracts/handlers/fee/FeeHandlerWithOra.content:
// SPDX-License-Identifier: LGPL-3.0-only
pragma solidity 0.8.11;
pragma experimental ABIEncoderV2;

import "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";
import "../../utils/AccessControl.sol";
import "../../interfaces/IFeeHandler.sol";
import "../../interfaces/IERCHandler.sol";
import "../../interfaces/IBridge.sol";
import "../../ERC20Safe.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

/**
    @title Handles deposit fees based on Effective rates provided by Fee oracle.
    @author ChainSafe Systems.
    @notice This contract is intended to be used with the Bridge contract.
 */
contract FeeHandlerWithOracle is IFeeHandler, AccessControl, ERC20Safe {
    address public immutable _bridgeAddress;

    address public _oracleAddress;
    uint256 public override _fee = 0;

    uint32 public _gasUsed;
    uint16 public _feePercent; // multiplied by 100 to avoid precision loss

    struct OracleMessageType {
        // Base Effective Rate - effective rate between base currencies of source and dest networks (eg. MATIC/ETH)
        uint256 ber;
        // Token Effective Rate - rate between base currency of destination network and token that is being trasferred (eg. MATIC/USDT)
        uint256 ter;
        uint256 dstGasPrice;
        uint256 expiresAt;
        uint8 fromDomainID;
        uint8 toDomainID;
        bytes32 resourceID;
    }

    struct FeeDataType {
        bytes message;
        bytes sig;
        uint256 amount;
    }

    /**
        @param bridgeAddress Contract address of previously deployed Bridge.
     */
    constructor(address bridgeAddress) {
        _bridgeAddress = bridgeAddress;
        _setupRole(DEFAULT_ADMIN_ROLE, msg.sender);
    }

    // Admin functions
    /**
        @notice Removes admin role from {_msgSender()} and grants it to {newAdmin}.
        @notice Only callable by an address that currently has the admin role.
        @param newAdmin Address that admin role will be granted to.
     */
    function renounceAdmin(address newAdmin) external onlyAdmin {
        address sender = _msgSender();
        require(sender != newAdmin, "Cannot renounce oneself");
        grantRole(DEFAULT_ADMIN_ROLE, newAdmin);
        renounceRole(DEFAULT_ADMIN_ROLE, sender);
    }

    /**
        @notice Sets the fee oracle address for signature verification.
        @param oracleAddress Fee oracle address.
     */
    function setFeeOracle(address oracleAddress) external onlyAdmin {
        _oracleAddress = oracleAddress;
    }

    /**
        @notice Sets the fee properties.
        @param gasUsed Gas used for transfer.
        @param feePercent Added to fee amount. total fee = fee + fee * feePercent
     */
    function setFeeProperties(uint32 gasUsed, uint16 feePercent)
        external
        onlyAdmin
    {
        _gasUsed = gasUsed;
        _feePercent = feePercent;
    }

    /**
        @notice Collects fee for deposit.
        @param sender Sender of the deposit.
        @param fromDomainID ID of the source chain.
        @param destinationDomainID ID of chain deposit will be bridged to.
        @param resourceID ResourceID to be used when making deposits.
        @param depositData Additional data to be passed to specified handler.
        @param feeData Additional data to be passed to the fee handler.
     */
    function collectFee(
        address sender,
        uint8 fromDomainID,
        uint8 destinationDomainID,
        bytes32 resourceID,
        bytes calldata depositData,
        bytes calldata feeData
    ) external payable onlyBridge {
        require(msg.value == 0, "collectFee: msg.value != 0");
        (uint256 fee, address tokenAddress) = _calculateFee(
            sender,
            fromDomainID,
            destinationDomainID,
            resourceID,
            depositData,
            feeData
        );
        lockERC20(tokenAddress, sender, address(this), fee);
        emit FeeCollected(
            sender,
            fromDomainID,
            destinationDomainID,
            resourceID,
            fee,
            tokenAddress
        );
    }

    /**
        @notice Calculates fee for deposit.
        @param sender Sender of the deposit.
        @param fromDomainID ID of the source chain.
        @param destinationDomainID ID of chain deposit will be bridged to.
        @param resourceID ResourceID to be used when making deposits.
        @param depositData Additional data to be passed to specified handler.
        @param feeData Additional data to be passed to the fee handler.
        @return fee Returns the fee amount.
        @return tokenAddress Returns the address of the token to be used for fee.
     */
    function calculateFee(
        address sender,
        uint8 fromDomainID,
        uint8 destinationDomainID,
        bytes32 resourceID,
        bytes calldata depositData,
        bytes calldata feeData
    ) external view returns (uint256 fee, address tokenAddress) {
        return
            _calculateFee(
                sender,
                fromDomainID,
                destinationDomainID,
                resourceID,
                depositData,
                feeData
            );
    }

    function _calculateFee(
        address sender,
        uint8 fromDomainID,
        uint8 destinationDomainID,
        bytes32 resourceID,
        bytes calldata depositData,
        bytes calldata feeData
    ) internal view returns (uint256 fee, address tokenAddress) {
        /** 
            Message:
            ber * 10^18:  uint256
            ter * 10^18:  uint256
            dstGasPrice:  uint256
            expiresAt:    uint256
            fromDomainID: uint8 encoded as uint256
            toDomainID:   uint8 encoded as uint256
            resourceID:   bytes32
            sig:          bytes(65 bytes)

            total in bytes:
            message:
            32 * 7  = 224
            message + sig:
            224 + 65 = 289

            amount: uint256
            total: 321
        */

        require(feeData.length == 321, "Incorrect feeData length");

        FeeDataType memory feeDataDecoded;

        feeDataDecoded.message = bytes(feeData[:224]);
        feeDataDecoded.sig = bytes(feeData[224:289]);
        feeDataDecoded.amount = abi.decode(feeData[289:], (uint256));

        OracleMessageType memory oracleMessage = abi.decode(
            feeDataDecoded.message,
            (OracleMessageType)
        );
        require(
            block.timestamp <= oracleMessage.expiresAt,
            "Obsolete oracle data"
        );
        require(
            (oracleMessage.fromDomainID == fromDomainID) &&
                (oracleMessage.toDomainID == destinationDomainID) &&
                (oracleMessage.resourceID == resourceID),
            "Incorrect deposit params"
        );

        bytes32 messageHash = keccak256(feeDataDecoded.message);

        verifySig(messageHash, feeDataDecoded.sig, _oracleAddress);

        address tokenHandler = IBridge(_bridgeAddress)
            ._resourceIDToHandlerAddress(resourceID);
        address tokenAddress = IERCHandler(tokenHandler)
            ._resourceIDToTokenContractAddress(resourceID);

        // txCost = dstGasPrice * _gasUsed * Token Effective Rate (rate of dest base currency to token)
        uint256 txCost = (oracleMessage.dstGasPrice *
            _gasUsed *
            oracleMessage.ter) / 1e18;

        fee = (feeDataDecoded.amount * _feePercent) / 1e4; // 100 for percent and 100 to avoid precision loss

        if (fee < txCost) {
            fee = txCost;
        }
        return (fee, tokenAddress);
    }

    /**
        @notice Transfers tokens from the contract to the specified addresses. The parameters addrs and amounts are mapped 1-1.
        This means that the address at index 0 for addrs will receive the amount of tokens from amounts at index 0.
        @param resourceID ResourceID of the token.
        @param addrs Array of addresses to transfer {amounts} to.
        @param amounts Array of amounts to transfer to {addrs}.
     */
    function transferFee(
        bytes32 resourceID,
        address[] calldata addrs,
        uint256[] calldata amounts
    ) external onlyAdmin {
        require(
            addrs.length == amounts.length,
            "addrs[], amounts[]: diff length"
        );
        address tokenHandler = IBridge(_bridgeAddress)
            ._resourceIDToHandlerAddress(resourceID);
        address tokenAddress = IERCHandler(tokenHandler)
            ._resourceIDToTokenContractAddress(resourceID);
        for (uint256 i = 0; i < addrs.length; i++) {
            releaseERC20(tokenAddress, addrs[i], amounts[i]);
            emit FeeDistributed(tokenAddress, addrs[i], amounts[i]);
        }
    }

    function verifySig(
        bytes32 message,
        bytes memory signature,
        address signerAddress
    ) internal pure {
        address signerAddressRecovered = ECDSA.recover(message, signature);
        require(signerAddressRecovered == signerAddress, "Invalid signature");
    }

    modifier onlyAdmin() {
        require(
            hasRole(DEFAULT_ADMIN_ROLE, msg.sender),
            "sender doesn't have admin role"
        );
        _;
    }

    modifier onlyBridge() {
        _onlyBridge();
        _;
    }

    function _onlyBridge() private view {
        require(msg.sender == _bridgeAddress, "sender must be bridge contract");
    }
}

