contracts/handlers/ERC20HandlerUpgradeab.content:
// SPDX-License-Identifier: LGPL-3.0-only
pragma solidity 0.8.11;
pragma experimental ABIEncoderV2;

import "../interfaces/IDepositExecute.sol";
import {HandlerHelpersUpgradeable as HandlerHelpers} from "./HandlerHelpersUpgradeable.sol";
import "../ERC20Safe.sol";

/**
    @title Handles ERC20 deposits and deposit executions.
    @author ChainSafe Systems.
    @notice This contract is intended to be used with the Bridge contract.
 */
contract ERC20HandlerUpgradeable is IDepositExecute, HandlerHelpers, ERC20Safe {
    /**
        @param bridgeAddress Contract address of previously deployed Bridge.
     */
    function initialize(address bridgeAddress) public initializer {
        __HandlerHelpers_init(bridgeAddress);
    }

    error ProvidedTokenAddressIsNotWhitelisted();

    receive() external payable {}

    /**
        @notice A deposit is initiated by making a deposit in the Bridge contract.
        @param resourceID ResourceID used to find address of token to be used for deposit.
        @param depositor Address of account making the deposit in the Bridge contract.
        @param data Consists of {amount} padded to 32 bytes.
        @notice Data passed into the function should be constructed as follows:
        amount                      uint256     bytes   0 - 32
        @dev Depending if the corresponding {tokenAddress} for the parsed {resourceID} is
        marked true in {_burnList}, deposited tokens will be burned, if not, they will be locked.
        @return an empty data.
     */
    function deposit(
        bytes32 resourceID,
        address depositor,
        bytes calldata data
    ) external payable override onlyBridge returns (bytes memory) {
        uint256 amount;
        (amount) = abi.decode(data, (uint256));

        address tokenAddress = _resourceIDToTokenContractAddress[resourceID];
        if (!_contractWhitelist[tokenAddress]) {
            revert ProvidedTokenAddressIsNotWhitelisted();
        }

        if (_burnList[tokenAddress]) {
            burnERC20(tokenAddress, depositor, amount);
        } else if (isNative[tokenAddress]) {
            depositETH(amount);
        } else {
            lockERC20(tokenAddress, depositor, address(this), amount);
        }
        return new bytes(0);
    }

    /**
        @notice Proposal execution should be initiated when a proposal is finalized in the Bridge contract.
        by a relayer on the deposit's destination chain.
        @param data Consists of {resourceID}, {amount}, {lenDestinationRecipientAddress},
        and {destinationRecipientAddress} all padded to 32 bytes.
        @notice Data passed into the function should be constructed as follows:
        amount                                 uint256     bytes  0 - 32
        destinationRecipientAddress length     uint256     bytes  32 - 64
        destinationRecipientAddress            bytes       bytes  64 - END
     */
    function executeProposal(bytes32 resourceID, bytes calldata data)
        external
        override
        onlyBridge
    {
        uint256 amount;
        uint256 lenDestinationRecipientAddress;
        bytes memory destinationRecipientAddress;

        (amount, lenDestinationRecipientAddress) = abi.decode(
            data,
            (uint256, uint256)
        );
        destinationRecipientAddress = bytes(
            data[64:64 + lenDestinationRecipientAddress]
        );

        bytes20 recipientAddress;
        address tokenAddress = _resourceIDToTokenContractAddress[resourceID];

        assembly {
            recipientAddress := mload(add(destinationRecipientAddress, 0x20))
        }

        require(
            _contractWhitelist[tokenAddress],
            "provided tokenAddress is not whitelisted"
        );

        if (_burnList[tokenAddress]) {
            mintERC20(tokenAddress, address(recipientAddress), amount);
        } else if (isNative[tokenAddress]) {
            withdrawETH(address(recipientAddress), amount);
        } else {
            releaseERC20(tokenAddress, address(recipientAddress), amount);
        }
    }

    /**
        @notice Used to manually release ERC20 tokens from ERC20Safe.
        @param data Consists of {tokenAddress}, {recipient}, and {amount} all padded to 32 bytes.
        @notice Data passed into the function should be constructed as follows:
        tokenAddress                           address     bytes  0 - 32
        recipient                              address     bytes  32 - 64
        amount                                 uint        bytes  64 - 96
     */
    function withdraw(bytes memory data) external override onlyBridge {
        address tokenAddress;
        address recipient;
        uint256 amount;

        (tokenAddress, recipient, amount) = abi.decode(
            data,
            (address, address, uint256)
        );

        releaseERC20(tokenAddress, recipient, amount);
    }

    /**
        @notice Used to manually release ETH ERC20Safe.
        @param data Consists of {recipient}, and {amount} all padded to 32 bytes.
        @notice Data passed into the function should be constructed as follows:
        recipient                           address     bytes  0 - 32
        amount                                 uint     bytes  32 - 64
     */
    function withdrawETH(bytes memory data) external override onlyBridge {
        address recipient;
        uint256 amount;

        (recipient, amount) = abi.decode(data, (address, uint256));

        withdrawETH(recipient, amount);
    }
}

contracts/handlers/ERC1155HandlerUpgrade.content:
// SPDX-License-Identifier: LGPL-3.0-only
pragma solidity 0.8.11;
pragma experimental ABIEncoderV2;

import "../interfaces/IDepositExecute.sol";
import {HandlerHelpersUpgradeable as HandlerHelpers} from "./HandlerHelpersUpgradeable.sol";
import "../ERC1155Safe.sol";
import {ERC165CheckerUpgradeable as ERC165Checker} from "@openzeppelin/contracts-upgradeable/utils/introspection/ERC165CheckerUpgradeable.sol";
import {ERC1155HolderUpgradeable as ERC1155Holder} from "@openzeppelin/contracts-upgradeable/token/ERC1155/utils/ERC1155HolderUpgradeable.sol";

contract ERC1155HandlerUpgradeable is
    IDepositExecute,
    HandlerHelpers,
    ERC1155Safe,
    ERC1155Holder
{
    using ERC165Checker for address;

    bytes4 private constant _INTERFACE_ERC1155_METADATA = 0x0e89341c;
    bytes private constant EMPTY_BYTES = "";

    /**
        @param bridgeAddress Contract address of previously deployed Bridge.
     */
    function initialize(address bridgeAddress) public initializer {
        __HandlerHelpers_init(bridgeAddress);
    }

    /**
        @notice A deposit is initiated by making a deposit in the Bridge contract.
        @param resourceID ResourceID used to find address of token to be used for deposit.
        @param depositor Address of account making the deposit in the Bridge contract.
        @param data Consists of ABI-encoded arrays of tokenIDs and amounts.
     */
    function deposit(
        bytes32 resourceID,
        address depositor,
        bytes calldata data
    ) external payable override onlyBridge returns (bytes memory) {
        require(msg.value == 0, "msg.value != 0");
        uint256[] memory tokenIDs;
        uint256[] memory amounts;

        (tokenIDs, amounts) = abi.decode(data, (uint256[], uint256[]));

        address tokenAddress = _resourceIDToTokenContractAddress[resourceID];
        require(
            tokenAddress != address(0),
            "provided resourceID does not exist"
        );

        if (_burnList[tokenAddress]) {
            burnBatchERC1155(tokenAddress, depositor, tokenIDs, amounts);
        } else {
            lockBatchERC1155(
                tokenAddress,
                depositor,
                address(this),
                tokenIDs,
                amounts,
                EMPTY_BYTES
            );
        }
        return new bytes(0);
    }

    /**
        @notice Proposal execution should be initiated when a proposal is finalized in the Bridge contract.
        by a relayer on the deposit's destination chain.
        @param data Consists of ABI-encoded {tokenIDs}, {amounts}, {recipient},
        and {transferData} of types uint[], uint[], bytes, bytes.
        @notice Data passed into the function should be constructed as ABI encoding of:
        tokenIDs                                    uint256[]  bytes
        amounts                                     uint256[]  bytes
        destinationRecipientAddress                   bytes    bytes
        transferData                                  bytes    bytes
     */
    function executeProposal(bytes32 resourceID, bytes calldata data)
        external
        override
        onlyBridge
    {
        uint256[] memory tokenIDs;
        uint256[] memory amounts;
        bytes memory recipient;
        bytes memory transferData;

        (tokenIDs, amounts, recipient, transferData) = abi.decode(
            data,
            (uint256[], uint256[], bytes, bytes)
        );

        bytes20 recipientAddress;

        assembly {
            recipientAddress := mload(add(recipient, 0x20))
        }

        address tokenAddress = _resourceIDToTokenContractAddress[resourceID];
        require(
            _contractWhitelist[address(tokenAddress)],
            "provided tokenAddress is not whitelisted"
        );

        if (_burnList[tokenAddress]) {
            mintBatchERC1155(
                tokenAddress,
                address(recipientAddress),
                tokenIDs,
                amounts,
                transferData
            );
        } else {
            releaseBatchERC1155(
                tokenAddress,
                address(this),
                address(recipientAddress),
                tokenIDs,
                amounts,
                transferData
            );
        }
    }

    /**
        @notice Used to manually release ERC1155 tokens from ERC1155Safe.
        @param data Consists of ABI-encoded {tokenAddress}, {recipient}, {tokenIDs}, 
        {amounts}, and {transferData} of types address, address, uint[], uint[], bytes.
     */
    function withdraw(bytes memory data) external override onlyBridge {
        address tokenAddress;
        address recipient;
        uint256[] memory tokenIDs;
        uint256[] memory amounts;
        bytes memory transferData;

        (tokenAddress, recipient, tokenIDs, amounts, transferData) = abi.decode(
            data,
            (address, address, uint256[], uint256[], bytes)
        );

        releaseBatchERC1155(
            tokenAddress,
            address(this),
            recipient,
            tokenIDs,
            amounts,
            transferData
        );
    }
}

contracts/ERC1155Safe.sol.content:
// SPDX-License-Identifier: LGPL-3.0-only
pragma solidity 0.8.11;

import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "@openzeppelin/contracts/token/ERC1155/IERC1155.sol";
import "./interfaces/IERCMintBurn.sol";

/**
    @title Manages deposited ERC1155s.
    @author ChainSafe Systems.
    @notice This contract is intended to be used with ERC1155Handler contract.
 */
contract ERC1155Safe {
    using SafeMath for uint256;

    /**
        @notice Used to gain custody of deposited token with batching.
        @param tokenAddress Address of ERC1155 to transfer.
        @param owner Address of current token owner.
        @param recipient Address to transfer token to.
        @param tokenIDs IDs of tokens to transfer.
        @param amounts Amounts of tokens to transfer.
        @param data Additional data.
     */
    function lockBatchERC1155(
        address tokenAddress,
        address owner,
        address recipient,
        uint256[] memory tokenIDs,
        uint256[] memory amounts,
        bytes memory data
    ) internal {
        IERC1155 erc1155 = IERC1155(tokenAddress);
        erc1155.safeBatchTransferFrom(
            owner,
            recipient,
            tokenIDs,
            amounts,
            data
        );
    }

    /**
        @notice Transfers custody of token to recipient with batching.
        @param tokenAddress Address of ERC1155 to transfer.
        @param owner Address of current token owner.
        @param recipient Address to transfer token to.
        @param tokenIDs IDs of tokens to transfer.
        @param amounts Amounts of tokens to transfer.
        @param data Additional data.
     */
    function releaseBatchERC1155(
        address tokenAddress,
        address owner,
        address recipient,
        uint256[] memory tokenIDs,
        uint256[] memory amounts,
        bytes memory data
    ) internal {
        IERC1155 erc1155 = IERC1155(tokenAddress);
        erc1155.safeBatchTransferFrom(
            owner,
            recipient,
            tokenIDs,
            amounts,
            data
        );
    }

    /**
        @notice Used to create new ERC1155s with batching.
        @param tokenAddress Address of ERC1155 to mint.
        @param recipient Address to mint token to.
        @param tokenIDs IDs of tokens to mint.
        @param amounts Amounts of token to mint.
        @param data Additional data.
     */
    function mintBatchERC1155(
        address tokenAddress,
        address recipient,
        uint256[] memory tokenIDs,
        uint256[] memory amounts,
        bytes memory data
    ) internal {
        IERCMintBurn erc1155 = IERCMintBurn(tokenAddress);
        erc1155.mintBatch(recipient, tokenIDs, amounts, data);
    }

    /**
        @notice Used to burn ERC1155s with batching.
        @param tokenAddress Address of ERC1155 to burn.
        @param owner owner of token.
        @param tokenIDs IDs of tokens to burn.
        @param amounts Amounts of tokens to burn.
     */
    function burnBatchERC1155(
        address tokenAddress,
        address owner,
        uint256[] memory tokenIDs,
        uint256[] memory amounts
    ) internal {
        IERCMintBurn erc1155 = IERCMintBurn(tokenAddress);
        erc1155.burnBatch(owner, tokenIDs, amounts);
    }
}

